import React from 'react';
import ReactDOM from 'react-dom';
import logo from './GitHub-Mark.png';
import './index.css';


import { useState } from 'react';

function App() {
    const [formValues, setFormValues] = useState({
        repo_name: '',
        repo_description: '',
        repo_private: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues((prevValues) => ({
            ...prevValues,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/', {  // Send the POST request to the root URL of your Flask server
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formValues),
        })
            .then((response) => response.text())
            .then((result) => {
                // Handle the response, e.g., display a success message
                console.log(result);
            })
            .catch((error) => {
                // Handle errors
                console.error(error);
            });
    };

    return (
        <div className="App">
            <nav className="Nav">
                <img src={logo} className="Nav-logo" alt="logo" width={90} />
                <h1 className="Header">Create your Github Repo</h1>
            </nav>
            <form onSubmit={handleSubmit} action="http://localhost:5000/" method="POST">
                <label htmlFor="repo_name">Repo name:</label>
                <input
                    type="text"
                    id="repo_name"
                    name="repo_name"
                    value={formValues.repo_name}
                    onChange={handleChange}
                    required
                />
                <br />
                <label htmlFor="repo_description">Repo description:</label>
                <input
                    type="text"
                    id="repo_description"
                    name="repo_description"
                    value={formValues.repo_description}
                    onChange={handleChange}
                    required
                />
                <br />
                <label htmlFor="repo_private">Repo private (Y/n):</label>
                <input
                    type="text"
                    id="repo_private"
                    name="repo_private"
                    value={formValues.repo_private}
                    onChange={handleChange}
                    required
                />
                <br />
                <input type="submit" value="Create Repo" />
            </form>
        </div>
    );
}

export default App;



ReactDOM.render(<App/>,document.getElementById('root'));
