import React from 'react';
import ReactDOM from 'react-dom';
import logo from './GitHub-Mark.png';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';
import CreateRepo from "./CreateRepo";
import AddFiles from "./AddFiles";
import Contributors from "./AddContributors";

import { useState } from 'react';

function Header() {
   return (
       <div className={"Header"}>
           <nav className="Nav">
               <img src={logo} className="Nav-logo" alt="logo" width={90} />
               <h1 className="Header">Create your Github Repo</h1>
           </nav>
      </div>
   );
}




function App() {
    const [formValues, setFormValues] = useState({
        repo_name: '',
        repo_description: '',
        repo_private: '',
        language: '',
        readme: '',
        readme_content: '',
    });
    const [showAddFiles, setShowAddFiles] = useState(false);
    const [showAddContributors, setShowAddContributors] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues((prevValues) => ({
            ...prevValues,
            [name]: value,
        }));
    };

    const handleSubmit = (e,uri,setShowOtherComponent) => {
        e.preventDefault();
        fetch(`http://localhost:5000/api/${uri}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formValues),
        })
            .then((response) => response.json())
            .then((result) => {
                if (result['status'] === 'success') {
                    setShowOtherComponent(true);
                }
            })
            .catch((error) => {
                console.error(error);
            });
    };
    return (
        <div className="App">
            <Header/>
            <CreateRepo/>
            <AddFiles/>
            <Contributors/>
        </div>
    );
}

export default App;



ReactDOM.render(<App/>,document.getElementById('root'));
