import React, {useState} from "react";

function AddFiles() {

    const [showOtherComponent, setShowOtherComponent] = useState(false);

    const [formValues, setFormValues] = useState({
        language: '',
        readme: '',
        readme_content: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues((prevValues) => ({
            ...prevValues,
            [name]: value,
        }));
    };
    const handleRest= (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/api/create-files', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formValues),
        })
            .then((response) => response.json())
            .then((result) => {
                if (result['status'] === 'success') {
                    setShowOtherComponent(true);
                }
            })
            .catch((error) => {
                console.error(error);
            });
    }

    const onClickEvent = (e) => {
        document.getElementById("readme_content").style.visibility = "visible";
        formValues.readme = "True";
    }

    return (
        <div className={"form-group"}>
            <form className={"forms"} onSubmit={handleRest}>
                <label className={"labels"} htmlFor="languae">Language:</label>
                <input
                    type="text"
                    id="language"
                    name="language"
                    onChange={handleChange}
                    className={"form-control"}
                    required
                />
                <br />
                <input
                    className={"button"}
                    type={"button"}
                    value={"Add Readme"}
                    id = "readme"
                    name = "readme"
                    onClick = {onClickEvent}
                />
                <br />
                <label htmlFor="readme_content">Readme content:</label>
                <input
                    type="text"
                    id="readme_content"
                    name="readme_content"
                    onChange={handleChange}
                    style={{visibility: "hidden"}}
                    className={"form-control"}
                />
                <br />
                <input type="submit" value="Submit" onClick={() => setShowOtherComponent(true)}/>
            </form>
            {showOtherComponent ? <Contributors/> : null}
        </div>
    );
}
export default AddFiles;
