import React from 'react';
import ReactDOM from 'react-dom';
import logo from './GitHub-Mark.png';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';


import { useState } from 'react';






function App() {
    const [formValues, setFormValues] = useState({
        repo_name: '',
        repo_description: '',
        repo_private: '',
    });
    const [showOtherComponent, setShowOtherComponent] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues((prevValues) => ({
            ...prevValues,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:5000/api/create-repo', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formValues),
        })
            .then((response) => response.json())
            .then((result) => {
                if (result['status'] === 'success') {
                    setShowOtherComponent(true);
                }
            })
            .catch((error) => {
                console.error(error);
            });
    };

    return (
        <div className="App">
            <nav className="Nav">
                <img src={logo} className="Nav-logo" alt="logo" width={90} />
                <h1 className="Header">Create your Github Repo</h1>
            </nav>
            <form className="forms" onSubmit={handleSubmit} >
                <label className={"labels"} htmlFor="repo_name">Repo name:</label>
                <input
                    type="text"
                    id="repo_name"
                    name="repo_name"
                    onChange={handleChange}
                    className={"form-control"}
                    required
                />
                <br />
                <label className={"labels"} htmlFor="repo_description">Repo description:</label>
                <input
                    type="text"
                    id="repo_description"
                    name="repo_description"
                    onChange={handleChange}
                    className={"form-control"}
                    required
                />
                <br />
                <label className={"labels"} htmlFor="repo_private">Repo private (Y/n):</label>
                <input
                    type="text"
                    id="repo_private"
                    name="repo_private"
                    onChange={handleChange}
                    className={"form-control"}
                    required
                />
                <br />
                <input className={"button"} type="submit" value="Create Repo" onClick={() => setShowOtherComponent(true)} />
            </form>
            {showOtherComponent ? <AddFiles/> : null}
        </div>
    );
}

export default App;



ReactDOM.render(<App/>,document.getElementById('root'));
